 $(function(){        
            if($(window).width()>1024){
              plyr.setup('#mediaVideo');
            }
            if($(window).width()<1024){
                $('.news-info').show();
                $('.footer').show();            
            }
            window.onresize = function(){
                if($(window).width()<1025){
                  $('.news-info').show();
                  $('.footer').show();              
                }              
            }
            if($(window).width()>768){
                $('#banvideo').css({"objectFit":"fill","width":"100%"});
                $("#banvideo").on("ended",function(){
                setTimeout(function(){
                  if ($(window).width()>992) {
                    $('.home-head').slideDown(1000);
                  }
                  if($(window).width()>768){
                    $('.glory').slideDown(1000);
                    $('.news-info').show();
                    $('.footer').show();
                  }
                },1000);
              });              
            }
            window.onresize = function(){
                
                if($(window).width()>768){
                  $('#banvideo').css({"objectFit":"fill","width":"100%"});
                  $("#banvideo").on("ended",function(){
                  setTimeout(function(){
                    if ($(window).width()>992) {
                      $('.home-head').slideDown(1000);
                    }
                    if($(window).width()>768){
                      $('.glory').slideDown(1000);
                      $('.news-info').show();
                      $('.footer').show();
                    }
                  },1000);
                });              
              }  
              if ($(window).width()<=990) {
                $('.glory').hide();
              }        
            }            
            // 点击消失
            $('.drop').on("click",function(){
              $('.glory').fadeOut(1000);
            });
            // 锚点设置
            $('.drop').anchorScroll({
              scrollSpeed: 800 // 锚点速度
            });

            
            window.onscroll = function(){
              if (scroll().top  > 200) {
                $('.glory').fadeOut(1000);
              }

              if (scroll().top > 120) {
                $('.flex-nav').css("display","block");
              } else {
                 $('.flex-nav').css("display","none");
              } 
            };

            // 滚动事件兼容
            function scroll() {
                return {
                    top: window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0,
                    left: window.pageYOffset || document.documentElement.scrollLeft || document.body.scrollLeft || 0
                };
            }



        });
        // 头部导航栏下拉框
        
        